export const aboutSectionBoxStyles = (reverse) => ({
  backgroundColor: (theme) =>
    // theme.palette.mode === 'dark' ? 'rgb(0,3,30)' : '',
    `${
      reverse === 'reverse' ? theme.palette.primary.main : 'background.default'
    }`,
  color: `${reverse === 'reverse' ? '#000000' : '#ffffff'}`,
  // borderRadius: '6px',
  width: '100%',
  height: 'calc(100vh - 64px)',
  boxSizing: 'border-box',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'stretch',
  alignItems: 'center',
  margin: '0 auto',
});

export const aboutContentBoxStyles = {
  // width: '65%',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'flex-start',
  backgroundColor: 'primary.main',
  color: '#000000',
  padding: '5%',
  boxSizing: 'border-box',
  height: '100%',
};

export const aboutCardHeaderStyles = { mb: '7%' };
export const aboutCardTextStyles = { marginBottom: '20px' };
