export const headerAppBarStyles = {
  backdropFilter: 'blur(2px)',
};

export const headerCompanyNameStyles = {
  // mr: '25%',
  display: 'flex',
  fontFamily: 'monospace',
  fontWeight: 700,
  letterSpacing: '.3rem',
  color: 'inherit',
  textDecoration: 'none',
};

export const headerToolbarStyles = {
  display: 'flex',
  justifyContent: 'space-between',
  width: '100%',
  boxSizing: 'border-box',
};
export const headerToolbarBoxStyles = {
  display: 'flex',
  alignItems: 'center',
};

export const navLinkStyles = {
  textDecoration: 'none',
  color: 'inherit',
  letterSpacing: '.2rem',
  textTransform: 'uppercase',
  fontWeight: 700,
};

export const buttonHeaderStyles = (location) => ({
  visibility: location.pathname === '/' ? 'hidden' : 'visible',
});
