export const contactsSectionBoxStyles = (reverse) => ({
  backgroundColor: (theme) =>
    `${
      reverse === 'reverse' ? theme.palette.primary.main : 'background.default'
    }`,
  // color: `${reverse === 'reverse' ? '#000000' : '#ffffff'}`,
  // borderRadius: '6px',
  width: '100%',
  // height: 'calc(100vh - 64px)',
  padding: '60px',
  boxSizing: 'border-box',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
});

export const contactsHeaderStyles = { mb: '3%', mt: '3%' };
export const contactsTextStyles = {
  marginBottom: '20px',
  // color: 'primary.main',
};
