export const headerAppBarStyles = {
  backdropFilter: 'blur(2px)',
};

export const headerCompanyNameStyles = {
  // mr: '25%',
  display: 'flex',
  // fontFamily: 'monospace',
  fontWeight: 800,
  letterSpacing: '.2rem',
  color: 'inherit',
  textDecoration: 'none',
};

export const headerToolbarStyles = {
  display: 'flex',
  justifyContent: 'space-between',
  width: '100%',
  maxWidth: '1440px',
  margin: '0 auto',
  boxSizing: 'border-box',
  outline: '1px solid blue',
};
export const headerToolbarBoxStyles = {
  display: 'flex',
  alignItems: 'center',
  outline: '1px solid violet',
};

export const navLinkStyles = {
  textDecoration: 'none',
  color: 'inherit',
  letterSpacing: '.2rem',
  textTransform: 'uppercase',
  fontWeight: 700,
  outline: '1px solid blue',
};

export const buttonHeaderStyles = (location) => ({
  visibility: location.pathname === '/' ? 'hidden' : 'visible',
});
